#!/bin/bash
'ffprobe.exe' -hide_banner -v quiet -print_format xml -show_format -show_streams -show_entries "stream=index,codec_type,codec_name,codec_tag,codec_tag_string,profile,level,pix_fmt,sample_fmt,width,height,coded_width,coded_height,color_range,color_space,color_transfer,color_primaries,r_frame_rate,avg_frame_rate,time_base,codec_time_base,sample_aspect_ratio,display_aspect_rationchannels,sample_rate,channel_layout,bit_rate,bits_per_raw_sample,nb_frames : format=filename,size,nb_streams,format_name,duration,bit_rate : disposition= : format_tags=major_brand,minor_version : stream_tags=" "$@" | xmlstarlet ed -d '//*[not(@*) and not(*) and (not(normalize-space(text())) or .=-1)]'
